%este programa simula un estudio de 12 presas, 3 depredadores y 30 estaciones %cada corrida genera datos diferentesclear %%%%%ENTRADAS%número de presaspresas=12;%número de depredadoresdepred=3;%número de estacionesestaciones=30;%valor inicial de días de colocación de una camaradias=30;inte=50;%se asigna una probabilidad de ocurrencia a cada especieprob=[0.11,0.2,0.3,0.5,0.09,0.19,0.29,0.39,0.07,0.17,0.37,0.47,0.4,0.03,0.02];%inicializa una matriz vacia para cada presa y depredadorMI=[];MI=zeros(estaciones*(dias),estaciones,presas+depred);destaciones=[];%%%%%COLOCACION DE PRESAS Y DEPREDADORES%se colocan aleatoriamente las presas y el depredador 13  for i=1:1:13    %para cada presa la variable "re" nos da un límite de apariciones en cada una de las estaciones    re=floor(rand(1,estaciones)*prob(1,i)*rows(MI)*0.015);    for j=1:1:estaciones    %para cada estación se coloca el número de apariciones indicado en un dia aleatorio dentro de los dias para cada estacion      if re(1,j)~=0        ma=ceil(rand(1,re(1,j))*inte+(inte-10)*(j-1));        MI(ma,j,i)=1;      end    endfor  endfor%se coloca el depredador 14n=0while n<ceil(prob(1,14)*rows(MI))  %la especie 14 pasa entre 10 y 12 días después de la especie 5   [x,y]=find(MI(:,:,5)==1);  r=rand(1,1);  MI(x(ceil(r*(rows(x)-1)),1)+10+ceil(rand(1,1)*2), y(ceil(r*(rows(x)-1)),1), 14)=1;  %la especie 14 pasa entre 10 y 12 días antes de la especie 6   [x,y]=find(MI(:,:,6)==1);  r=rand(1,1);  X=x(ceil(r*(rows(x)-1)),1)-12+ceil(rand(1,1)*2);    if X>1      MI(X, y(ceil(r*(rows(x)-1)),1), 14)=1;    end  %la especie 14 pasa entre 5 y 7 días antes de la especie 7   [x,y]=find(MI(:,:,7)==1);  r=rand(1,1);  X=x(ceil(r*(rows(x)-1)),1)-5+ceil(rand(1,1)*2);    if X>1      MI(X, y(ceil(r*(rows(x)-1)),1), 14)=1;    end  %la especie 14 pasa el mismo día que la especie 8  [x,y]=find(MI(:,:,8)==1);   r=rand(1,1);   MI(x(ceil(r*(rows(x)-1)),1), y(ceil(r*(rows(x)-1)),1), 14)=1;   n=sum(sum(MI(:,:,14)));endwhile%se coloca el depredador 14n=0while n<ceil(prob(1,15)*rows(MI))  %la especie 15 pasa entre 2 y 4 días después de la especie 9  [x,y]=find(MI(:,:,9)==1);  r=rand(1,1);  MI(x(ceil(r*(rows(x)-1)),1)+2+ceil(rand(1,1)*2), y(ceil(r*(rows(x)-1)),1), 15)=1;  %la especie 15 pasa entre 10 y 12 días antes de la especie 10  [x,y]=find(MI(:,:,10)==1);  r=rand(1,1);  X=x(ceil(r*(rows(x)-1)),1)-12+ceil(rand(1,1)*2);    if X>1      MI(X, y(ceil(r*(rows(x)-1)),1), 15)=1;    end  %la especie 15 pasa entre 3 y 5 días antes de la especie 11  [x,y]=find(MI(:,:,11)==1);  r=rand(1,1);  X=x(ceil(r*(rows(x)-1)),1)-5+ceil(rand(1,1)*2);    if X>1      MI(X, y(ceil(r*(rows(x)-1)),1), 15)=1;    end  %la especie 15 pasa el mismo día que la especie 12  [x,y]=find(MI(:,:,12)==1);  r=rand(1,1);  MI(x(ceil(r*(rows(x)-1)),1), y(ceil(r*(rows(x)-1)),1), 15)=1;  n=sum(sum(MI(:,:,15)));endwhile%%%%%SALIDAS%días que cada camara estuvo isntaladaMIS=MI(:,:,1);  for p=2:1:15    MIS=MIS+MI(:,:,p);  endfordias2=sign(MIS);E=[];  for l=1:1:30    e=find(dias2(:,l)==1);    E=[E;min(e),max(e),max(e)-min(e)+1];  endfor%frecuencias por especie(fila) y estación (columna)Frec=[];FrecR=[];  for ka=1:1:presas+depred    Frec=[Frec;sum(MI(:,:,ka))];    FrecR=[FrecR;sum(MI(:,:,ka))./E(:,3)'];  endfor%cambia los datos a una listaLista=[] for o=1:1:15  for w=1:1:30    h=find (MI(:,w,o)~=0);     if rows(h)~=0      for t=1:1:rows(h)    Lista=[Lista;h(t,1),w,o];      endfor    end  endfor  endfor  save MI2 MIsave fechas2 Esave Frec2 Frecsave Lista2 Lista